import { ServiceLinkerManagementClient } from "@azure/arm-servicelinker";
import { DefaultAzureCredential } from "@azure/identity";

/**
 * This sample demonstrates how to lists the configuration names generated by Service Connector for all target, client types, auth types.
 *
 * @summary lists the configuration names generated by Service Connector for all target, client types, auth types.
 * x-ms-original-file: 2024-07-01-preview/ConfigurationNamesList.json
 */
async function getConfigurationNames(): Promise<void> {
  const credential = new DefaultAzureCredential();
  const subscriptionId = "00000000-0000-0000-0000-00000000000";
  const client = new ServiceLinkerManagementClient(credential, subscriptionId);
  const resArray = new Array();
  for await (const item of client.configurationNames.list()) {
    resArray.push(item);
  }

  console.log(resArray);
}

async function main(): Promise<void> {
  await getConfigurationNames();
}

main().catch(console.error);
